INSERT INTO Recipes (title, description, ingredients, instructions, created_at, updated_at)
VALUES
    ('Spaghetti Bolognese', 'Classic Italian pasta dish with meat sauce.', '[{"ingredient": "spaghetti", "quantity": "1 pack"}, {"ingredient": "tomato sauce", "quantity": "2 cups"}, {"ingredient": "ground beef", "quantity": "1 lb"}, {"ingredient": "oregano", "quantity": "1 tsp"}, {"ingredient": "basil", "quantity": "1 tsp"}]', ARRAY['Boil spaghetti according to package instructions.', 'In a separate pan, brown ground beef.', 'Add tomato sauce and herbs to the beef.', 'Serve sauce over cooked spaghetti.'], CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
    ('Chicken Alfredo Pasta', 'Creamy pasta dish with grilled chicken.', '[{"ingredient": "pasta", "quantity": "1 pack"}, {"ingredient": "Alfredo sauce", "quantity": "2 cups"}, {"ingredient": "chicken breast", "quantity": "1 lb"}, {"ingredient": "garlic", "quantity": "2 cloves"}, {"ingredient": "Parmesan cheese", "quantity": "1/2 cup"}]', ARRAY['Cook pasta according to package instructions.', 'Grill chicken breast and slice into strips.', 'Heat Alfredo sauce and garlic in a pan.', 'Toss cooked pasta with sauce and top with grilled chicken strips. Garnish with Parmesan cheese.'], CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
    ('Vegetarian Lasagna', 'Layers of pasta with tomato sauce and vegetables.', '[{"ingredient": "lasagna noodles", "quantity": "1 pack"}, {"ingredient": "marinara sauce", "quantity": "2 cups"}, {"ingredient": "spinach", "quantity": "2 cups"}, {"ingredient": "mushrooms", "quantity": "1 cup"}, {"ingredient": "ricotta cheese", "quantity": "1 cup"}, {"ingredient": "mozzarella cheese", "quantity": "1 cup"}]', ARRAY['Preheat oven to 350°F.', 'Cook lasagna noodles according to package instructions.', 'Layer noodles, marinara sauce, spinach, mushrooms, and ricotta cheese in a baking dish. Repeat layers. Top with mozzarella cheese.', 'Bake for 30 minutes or until bubbly.'], CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
    ('Beef Tacos', 'Flavorful tacos with seasoned ground beef.', '[{"ingredient": "tortillas", "quantity": "1 pack"}, {"ingredient": "ground beef", "quantity": "1 lb"}, {"ingredient": "taco seasoning", "quantity": "1 packet"}, {"ingredient": "lettuce", "quantity": "1 head"}, {"ingredient": "tomato", "quantity": "1"}, {"ingredient": "cheese", "quantity": "1 cup"}, {"ingredient": "salsa", "quantity": "1 cup"}]', ARRAY['Brown ground beef in a skillet.', 'Add taco seasoning and water according to package instructions.', 'Heat tortillas in a dry skillet.', 'Fill tortillas with seasoned beef, lettuce, tomato, cheese, and salsa.'], CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
    ('Margherita Pizza', 'Classic pizza topped with fresh tomatoes and basil.', '[{"ingredient": "pizza dough", "quantity": "1"}, {"ingredient": "tomato sauce", "quantity": "1/2 cup"}, {"ingredient": "mozzarella cheese", "quantity": "1 cup"}, {"ingredient": "tomatoes", "quantity": "2"}, {"ingredient": "basil", "quantity": "1/4 cup"}, {"ingredient": "olive oil", "quantity": "2 tbsp"}]', ARRAY['Preheat oven to 450°F.', 'Roll out pizza dough on a floured surface.', 'Spread tomato sauce over the dough.', 'Arrange sliced tomatoes and torn basil leaves on top. Drizzle with olive oil.', 'Bake for 12-15 minutes or until crust is golden brown.'], CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);